import React from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles(theme => ({
    container: {
        margin:"20px"
      },
        manfId:{
            width: "194p",
            height: "21px",
            fontFamily: "Roboto",
            fontSize: "18px",
            fontWeight: "500",
            fontStyle: "normal",
            fontStretch: "normal",
            lineHeight: "1.17",
            letterSpacing: "normal",
            textAlign: "center",
            color: "#6a6a6a",
        },
        date:{

            fontFamily: "Roboto",
            fontSize: "18px",
            fontWeight: "500",
            fontStyle: "normal",
            fontStretch: "normal",
            lineHeight: "1.17",
            letterSpacing: "normal",
            textAlign: "center",
            color: "#6a6a6a",
        },
        firstRow:{
            textAlign: "left"
        }

}));



export default function TextFields() {
    const classes = useStyles();
    const [values, setValues] = React.useState({
        name: '',
        email: '',
        phone: '',
        address: '',
    });

    const handleChange = name => event => {
        setValues({ ...values, [name]: event.target.value });
    };

return (
    <form container className={classes.container} noValidate autoComplete="off">
        <Grid item xs = {12} className = {classes.firstRow}>
            <div className = {classes.manfId}>Manufacturer Id: 12345</div>
            <div className = {classes.date}>Date: 12/12/2019</div>
        </Grid>
        <Grid xs = {12} item>
        <TextField
            required
            id="standard-required"
            label="Company Name"
            className={classes.textField}
            margin="normal"
            placeholder = "Company Name"
            onChange={handleChange('name')}
        />
        </Grid>
        <Grid xs = {12} item>
        <TextField
            required
            id="standard-required"
            label="Email"
            className={classes.textField}
            margin="normal"
            placeholder = "Email"
            onChange={handleChange('email')}
        />
        </Grid>
        <Grid xs = {12} item>
        <TextField
            required
            id="standard-required"
            label="Phone"
            className={classes.textField}
            margin="normal"
            placeholder = "Phone"
            onChange={handleChange('phone')}
        />
        </Grid>
        <Grid xs = {12} item>
        <TextField
            required
            id="standard-required"
            label="Address"
            className={classes.textField}
            margin="normal"
            placeholder = "Address"
            onChange={handleChange('address')}
        />
        </Grid>
    </form>
    );
}
